package org.khw.springwebfluxcoroutine.blog.domain.dtoimport com.fasterxml.jackson.annotation.JsonFormatimport java.time.LocalDateTimedata class KakaoBlogSearchResponse(    val documents: List<Document>)data class Document(    val blogname: String,    val contents: String,    @JsonFormat(pattern = "yyyy-MM-dd'T'HH:mm:ss.SSSXXX")    val datetime: LocalDateTime,    val title: String,    val url: String)