package org.khw.springwebfluxcoroutine.blog.domain.dtoimport com.fasterxml.jackson.annotation.JsonFormatimport java.time.LocalDatedata class NaverBlogSearchResponse(    val items: List<Item>)data class Item(    val title: String,    val description: String,    val link: String,    val bloggername: String,    @JsonFormat(pattern = "yyyyMMdd")    val postdate: LocalDate)