package org.khw.springwebfluxcoroutine.blog.handlerimport lombok.RequiredArgsConstructorimport org.khw.springwebfluxcoroutine.blog.service.BlogSearchServiceimport org.springframework.stereotype.Componentimport org.springframework.web.reactive.function.server.ServerRequestimport org.springframework.web.reactive.function.server.ServerResponseimport org.springframework.web.reactive.function.server.bodyValueAndAwaitimport org.springframework.web.reactive.function.server.buildAndAwait@Component@RequiredArgsConstructorclass BlogHandler(val blogSearchService: BlogSearchService) {    suspend fun searchBlogPost(request: ServerRequest): ServerResponse{        return ServerResponse.ok().bodyValueAndAwait(blogSearchService.blogSearch())    }}